void APWebServerSetup(void) {
    setupButton();
    webserverSetup();

    WiFi.mode(WIFI_STA);
    WiFi.begin();

    Serial.println("");

    int timeout = 0;
    // Wait for connection
    while (WiFi.status() != WL_CONNECTED && timeout <= 5) {
        delay(1000);
        timeout++;
        Serial.print(".");
    }

    if (WiFi.status() != WL_CONNECTED) {
        Serial.println("No se pudo conectar al wifi");
        changeToAPSTAMode();

    } else {
        Serial.println("");
        IPAddress IP = WiFi.localIP();
        Serial.print("STA IP address: ");
        Serial.println(IP);
        // printNetworkInfo();
    }
}

void APWebServerLoop(void) {
    buttonListener();
    if (WiFi.getMode() == WIFI_MODE_APSTA) {
        server.handleClient();
        if (canConnect) {
            changeToSTAMode();
            webserverStop();
            canConnect = false;
        }
    }
}

